package cn.edu.cqupt.mis.colorfullcloud.config;import com.alibaba.druid.support.http.WebStatFilter;import javax.servlet.annotation.WebFilter;import javax.servlet.annotation.WebInitParam;/** * @author DengSiYuan * @date :2019/3/11 22:25 */@WebFilter(filterName = "druidWebStatFilter", urlPatterns = "/*",        initParams = {                @WebInitParam(name = "exclusions", value = "*.js,*.gif,*bmp,*png,*css,*.ico,/druid/*")        })public class DruidStatFilter extends WebStatFilter {}